{	
  var sjFormat = "False"
  var gtfgffFormat = "False"
  var list = $job.inputs.sjdbGTFfile
  var paths_list = []
  var joined_paths = ""

  if (list) {
    list.forEach(function(f){return paths_list.push(f.path)})
    joined_paths = paths_list.join(" ")


    paths_list.forEach(function(f){
      ext = f.replace(/^.*\./, '')
      if (ext == "gff" || ext == "gtf") {
        gtfgffFormat = "True"
        return gtfgffFormat
      }
      if (ext == "txt") {
        sjFormat = "True"
        return sjFormat
      }
    })

    if ($job.inputs.sjdbGTFfile && $job.inputs.sjdbInsertSave != "None") {
      if (sjFormat == "True") {
        return "--sjdbFileChrStartEnd ".concat(joined_paths)
      }
      else if (gtfgffFormat == "True") {
        return "--sjdbGTFfile ".concat(joined_paths)
      }
    }
  }
}

{
  filename = $job.inputs.input_bam.path
  ext = $job.inputs.output_type

if (ext === "BAM")
{
    return filename.split('.').slice(0, -1).concat("sorted.bam").join(".").replace(/^.*[\\\/]/, '')
    }

else if (ext === "SAM")
{
    return filename.split('.').slice(0, -1).concat("sorted.sam").join('.').replace(/^.*[\\\/]/, '')
}

else 
{
	return filename.split('.').slice(0, -1).concat("sorted."+filename.split('.').slice(-1)[0]).join(".").replace(/^.*[\\\/]/, '')
}
}

{
  filename = $job.inputs.input_bam.path

  /* figuring out output file type */
  ext = $job.inputs.output_type
  if (ext === "BAM")
  {
    out_extension = "BAM"
  }
  else if (ext === "SAM")
  {
    out_extension = "SAM"
  }
  else 
  {
	out_extension = filename.split('.').slice(-1)[0].toUpperCase()
  }  

  /* if exist moving .bai in bam.bai */
  if ($job.inputs.create_index === 'True' && $job.inputs.sort_order === 'Coordinate' && out_extension == "BAM")
  {

    old_name = filename.split('.').slice(0, -1).concat('sorted.bai').join('.').replace(/^.*[\\\/]/, '')
    new_name = filename.split('.').slice(0, -1).concat('sorted.bam.bai').join('.').replace(/^.*[\\\/]/, '')
    return "; mv " + " " + old_name + " " + new_name
  }

}

{
  file = [].concat($job.inputs.reads)[0].path
  extension = /(?:\.([^.]+))?$/.exec(file)[1]
  if (extension == "gz") {
    return "--readFilesCommand zcat"
  } else if (extension == "bz2") {
    return "--readFilesCommand bzcat"
  }
}

