{
  var param_list = []

  function add_param(key, value){
    if (value == "") {
      return
    }
    else {
      return param_list.push(key.concat(":", value))
    }
  }

  add_param('ID', "1")
  if ($job.inputs.rg_seq_center) {
    add_param('CN', $job.inputs.rg_seq_center)
  } else if ([].concat($job.inputs.reads)[0].metadata.seq_center) {
    add_param('CN', [].concat($job.inputs.reads)[0].metadata.seq_center)
  }
  if ($job.inputs.rg_library_id) {
    add_param('LB', $job.inputs.rg_library_id)
  } else if ([].concat($job.inputs.reads)[0].metadata.library_id) {
    add_param('LB', [].concat($job.inputs.reads)[0].metadata.library_id)
  }
  if ($job.inputs.rg_mfl) {
    add_param('PI', $job.inputs.rg_mfl)
  } else if ([].concat($job.inputs.reads)[0].metadata.median_fragment_length) {
    add_param('PI', [].concat($job.inputs.reads)[0].metadata.median_fragment_length)
  }
  if ($job.inputs.rg_platform) {
    add_param('PL', $job.inputs.rg_platform.replace(/ /g,"_"))
  } else if ([].concat($job.inputs.reads)[0].metadata.platform) {
    add_param('PL', [].concat($job.inputs.reads)[0].metadata.platform.replace(/ /g,"_"))
  }
  if ($job.inputs.rg_platform_unit_id) {
    add_param('PU', $job.inputs.rg_platform_unit_id)
  } else if ([].concat($job.inputs.reads)[0].metadata.platform_unit_id) {
    add_param('PU', [].concat($job.inputs.reads)[0].metadata.platform_unit_id)
  }
  if ($job.inputs.rg_sample_id) {
    add_param('SM', $job.inputs.rg_sample_id)
  } else if ([].concat($job.inputs.reads)[0].metadata.sample_id) {
    add_param('SM', [].concat($job.inputs.reads)[0].metadata.sample_id)
  }
  return "--outSAMattrRGline ".concat(param_list.join(" "))
}

{
  function sharedStart(array){
  var A= array.concat().sort(), 
      a1= A[0], a2= A[A.length-1], L= a1.length, i= 0;
  while(i<L && a1.charAt(i)=== a2.charAt(i)) i++;
  return a1.substring(0, i);
  }
  path_list = []
  arr = [].concat($job.inputs.reads)
  arr.forEach(function(f){return path_list.push(f.path.replace(/\\/g,'/').replace( /.*\//, '' ))})
  common_prefix = sharedStart(path_list)
  intermediate = common_prefix.replace( /\-$|\_$|\.$/, '' ).concat("._STARgenome")
  source = "./".concat(intermediate)
  destination = intermediate.concat(".tar")
  if ($job.inputs.sjdbGTFfile && $job.inputs.sjdbInsertSave && $job.inputs.sjdbInsertSave != "None") {
    return "&& tar -vcf ".concat(destination, " ", source)
  }
}

