{
  function sharedStart(array){
  var A= array.concat().sort(), 
      a1= A[0], a2= A[A.length-1], L= a1.length, i= 0;
  while(i<L && a1.charAt(i)=== a2.charAt(i)) i++;
  return a1.substring(0, i);
  }
  path_list = []
  arr = [].concat($job.inputs.reads)
  arr.forEach(function(f){return path_list.push(f.path.replace(/\\/g,'/').replace( /.*\//, '' ))})
  common_prefix = sharedStart(path_list)
  mate1 = common_prefix.replace( /\-$|\_$|\.$/, '' ).concat(".Unmapped.out.mate1")
  mate2 = common_prefix.replace( /\-$|\_$|\.$/, '' ).concat(".Unmapped.out.mate2")
  mate1fq = mate1.concat(".fastq")
  mate2fq = mate2.concat(".fastq")
  if ($job.inputs.outReadsUnmapped == "Fastx" && arr.length > 1) {
    return "&& mv ".concat(mate1, " ", mate1fq, " && mv ", mate2, " ", mate2fq)
  }
  else if ($job.inputs.outReadsUnmapped == "Fastx" && arr.length == 1) {
    return "&& mv ".concat(mate1, " ", mate1fq)
  }
}

{	
  var list = [].concat($job.inputs.reads)

  var resp = []

  if (list.length == 1){
    resp.push(list[0].path)

  }else if (list.length == 2){    

    left = ""
    right = ""

    for (index = 0; index < list.length; ++index) {

      if (list[index].metadata != null){
        if (list[index].metadata.paired_end == 1){
          left = list[index].path
        }else if (list[index].metadata.paired_end == 2){
          right = list[index].path
        }
      }
    }

    if (left != "" && right != ""){      
      resp.push(left)
      resp.push(right)
    }
  }
  else if (list.length > 2){
    left = []
    right = []

    for (index = 0; index < list.length; ++index) {

      if (list[index].metadata != null){
        if (list[index].metadata.paired_end == 1){
          left.push(list[index].path)
        }else if (list[index].metadata.paired_end == 2){
          right.push(list[index].path)
        }
      }
    }
    left_join = left.join()
    right_join = right.join()
    if (left != [] && right != []){      
      resp.push(left_join)
      resp.push(right_join)
    }	
  }

  if(resp.length > 0){    
    return "--readFilesIn ".concat(resp.join(" "))
  }
}

{
