valueFrom.script:
{
  read_path = $job.inputs.aligned_reads.path
  read_name = read_path.split('.').slice(0, read_path.split('.').length-1).join('.').replace(/^.*[\\\/]/, '')
  source_name = "abundances.cxb"
  return "&& mv ".concat(source_name, " ", read_name, "_abundances.cxb")
}

{
  paths = []
  cspaths = []
  all_groups = []
  labels = []
  temp_group = []
  $job.inputs.sample_files.forEach( function(file){
    meta_key = file.metadata.sample_group
    all_groups.push(meta_key)
    basename = file.path.replace(/\\/g,'/').replace( /.*\//, '' )
    /*paths.push(basename);*/
  });
  function onlyUnique(value, index, self) { 
    return self.indexOf(value) === index;
  }
  labels = all_groups.filter(onlyUnique);
  /*return paths.join(',');*/
  labels.forEach( function(label){
    paths = []
    $job.inputs.sample_files.forEach( function(sample){
      if (sample.metadata.sample_group === label){
        paths.push(sample.path)
      }
    })
    cspaths.push(paths.join(','))
  })
  return labels.join(',').concat(" ", cspaths.join(' '));
}

{
  function sharedStart(array){
    var A= array.concat().sort(), 
    a1= A[0], a2= A[A.length-1], L= a1.length, i= 0;
    while(i<L && a1.charAt(i)=== a2.charAt(i)) i++;
    return a1.substring(0, i);
  }
  path_list = []
  $job.inputs.sample_files.forEach(function(f){return path_list.push(f.path.replace(/\\/g,'/').replace( /.*\//, '' ))})
  common_prefix = sharedStart(path_list)
  return common_prefix.concat(".cuffdiff.zip")
}
